mapper.insert:
insert into t_sys_user_role_rel(user_id , role_id , status)
values (#{userId} , #{roleId} , 1)
on duplicate key update status = 1;

insert into t_sys_user_role_rel(user_id , role_id , status) values
#{userId} , #{roleId} , 1
on duplicate key update status = 1;

insert into t_sys_role_privilege_rel(role_id , privilege_id , status) values
#{roleId} , #{privilegeId} , 1
on duplicate key update status = 1;

insert into t_sys_role_privilege_rel(role_id ,  privilege_id , status)
values (#{roleId} , #{privilegeId} , 1)
on duplicate key update status = 1;

mapper.update:
update t_sys_user_role_rel
set status = #{status}
where user_id = #{userId}
  and role_id = #{roleId}

update.foreach:
update t_sys_user_role_rel
set status = #{status}
where user_id = #{userId}
and role_id = #{roleId}

update t_sys_role_privilege_rel
set status = #{status}
where privilege_id = #{privilegeId}
and role_id = #{roleId}

mapper.update:
update t_sys_role_privilege_rel
set status = #{status}
where privilege_id = #{privilegeId}
  and role_id = #{roleId}
